import { ThisReceiver } from '@angular/compiler';
import { Component, importProvidersFrom } from '@angular/core';
import { CardapioserviceService } from 'src/app/services/cardapioservice.service';
import { Cardapio } from './cardapio';

@Component({
  selector: 'app-cardapio',
  templateUrl: './cardapio.component.html',
  styleUrls: ['./cardapio.component.css']
})
export class CardapioComponent {

  constructor(private cardapioservice: CardapioserviceService){

    this.getCardapio()

  }

    ngOnInit():void{}
    cardapio: Cardapio[] = [];
    getCardapio():void{
      this.cardapioservice.getAll().subscribe((cardapio)=> (this.cardapio = cardapio))
        }

}


<div class="container">
    <div class="row">
        <div *ngFor="let menu of cardapio" class="col-md4">
            <img [src]="menu.imagem">
            <h2>Nome: {{menu.prato}}</h2>
            <h2>preço: R$ {{menu.preco}} </h2>
        </div>
    </div>
</div>
