import { Component } from '@angular/core';
import { CardapioserviceService } from 'src/app/services/cardapioservice.service';
import { Cardapio } from './cardapio';



@Component({
  selector: 'app-cardapio',
  templateUrl:'./cardapio.component.html',
  styleUrls: ['./cardapio.component.css']
})
export class CardapioComponent {

  constructor(private Cardapioservice: CardapioserviceService){
    this.getCardapio()
  }


  ngOnInit():void{}

  cardapios: Cardapio[] = [];

  getCardapio():void{
    this.Cardapioservice.getAll().subscribe((Cardapio) => (this.cardapios = Cardapio));
  }

}
<h1>Comidas </h1>

<div class="container">
    <div class="row">
        <div *ngFor="let menu of cardapios" class="col-md-4">
            <img [src]="menu.imagem">
            <h2>prato: {{menu.prato}}</h2>
            <h2>preco: {{menu.preco}}</h2>

        </div>
    </div>
</div>
